import React, {useEffect} from 'react';
import { useLocation } from "react-router-dom";
import styled from "styled-components";
import PageContainer from '../components/PageContainer';
import Header from '../components/Header';
import { device } from '../common/breakpoints';

function Home({ modal, setModal }){
  const search = useLocation().search;
  const showLogIn = new URLSearchParams(search).get('showLogIn');
  useEffect(() => {
    if(showLogIn){
      setModal(prevState =>({
        ...prevState,
        isOpen : true,
        header : 'Sign In',
        form   : 'singIn'
      }));
    }
  }, [showLogIn]);
  
  return (
    <>
      <PageContainer>
        <HeaderContainer>
          <img src="/logo.png" alt="monkey with laptop logo" width="150"/>
          <div>
            <Header text="Code King"/>
          </div>          
          <h3 className="description">
            A coding challenge website built using microservices for
            <a href="https://sites.google.com/cs.umass.edu/compsci497s/home" > CS497S - Scalable Web Systems (Fall 2020)</a>
          </h3>
        </HeaderContainer>
      </PageContainer>

      <PageContainer>
        <Grid className="grid">
          <a href="#"
            className="card"
            onClick={() => {
              setModal( prevState =>({
                  ...prevState,
                  isOpen: true,
                  header : 'Create an Account',
                  form: 'signUp'
              })); 
            }}  
          >
          <img src="data:image/svg+xml;base64," width="100" />  
            <h3>Create an Account &rarr;</h3>
            <P>Unlock the ability to solve and create challenges and view highscores.</P>
          </a>

          <a href="/challenges" className="card">
            {/* <img src="data:image/svg+xml;base64," width="80" height="80"/> */}
            <img src="data:image/svg+xml;base64," width="100" />
            <h3>Solve &rarr;</h3>
            <P>Submit your own solutions to challenges! Beat other highscores!</P>
          </a>
          <a href='/createChallenge' className="card">
            <img src="data:image/svg+xml;base64," width="100"/>

            <h3>Contribute  &rarr;</h3>
            <P>Create a coding challenge of your own that other users can have a crack at!</P>
          </a>
          <a
            href="https://github.com/jitli98/CS497Project"
            className="card"
          >
            <img src="data:image/svg+xml;base64," width="100" />
            <h3>Documentation &rarr;</h3>
            <P>
              Get in-depth info on the microservices and APIs used to make this app. 
            </P>
          </a>
        </Grid>
      </PageContainer>
    </>
  );
}

const HeaderContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  align-content: center;
  flex-basis: 45%;
  text-align: center; 
  h3 {
    margin: 0 0 1rem 0;
    font-size: 1.5rem;
  }

  @media ${device.sm} {
    flex-basis: 100%;
    min-width: 0px;
  } 
`

const Grid = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
 

  & .card {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    margin: 1rem;
    flex-basis: 45%;
    min-height: 350px;
    min-width: 500px;
    height: auto;
    padding: 2rem;
    color: white;
    text-decoration: none;
    border: 4px solid black;
    border-radius: 10px;
    background: var(--secondary);
    transition: color 0.15s ease, border-color 0.15s ease;

    @media ${device.sm} {
      flex-basis: 100%;
      min-width: 0px;
    } 

    h3 {
      margin: 1rem 0 1rem 0;
      font-size: 1.5rem;
    }

    p {
      margin: 0;
      font-size: 1.1rem;
      line-height: 1.5;
    }

    :hover {
      transform: scale(1.03);
      transition: all .2s ease-in-out;
      box-shadow: 0 19px 38px rgba(0,0,0,0.30), 0 15px 12px rgba(0,0,0,0.22);
    }

    :hover,
    :focus,
    :active {
      background: var(--hover-color);
    }

  }
`;

const P = styled.p`
  text-align: center;
`;

export default Home;